        SecondArray =
            [SecondTable(SecondTitle: ["สมองอักเสบ", "เนื้องอกในสมอง", "ปวดศีรษะจากความเครียด"]),
            SecondTable(SecondTitle: ["มะเร็งตับ", "ปอดอักเสบ"]),
